    private static IEnumerable<int> isValidMaritimeFlagsPuzzle(List<char> letters)
    {
        for (int color = 0; color < 5; color++)
            if (letters.Count(l => _maritimeFlagsLetterColors[l - 'A'].Contains(color)) == 5)
                yield return color;
