    private readonly string TwitchHelpMessage = @"!{0} press 1 [number in reading order] | !{0} press TL [TL, TM, TR, BL, BM, BR] | !{0} press 1 2 3 | !{0} cycle | !{0} hover TL [hover over one button] | !{0} hover 1 | !{0} reset | !{0} colorblind";
